    public static Date parse(String date, String formatStr)
            throws ParseException {
        SimpleDateFormat format = new SimpleDateFormat(formatStr);
        return format.parse(date);
    }
    
    public static String getNow(String formatStr) {
        Date date = new Date();
        SimpleDateFormat format = new SimpleDateFormat(formatStr);
        return format.format(date).toString();
    }
    
    public static String getOfsHour(String timeStr, String pattern, int offset)
            throws ParseException {
        Date date = parse(timeStr, pattern);
        Calendar c = Calendar.getInstance();
        c.setTime(date);
        c.add(Calendar.HOUR_OF_DAY, offset);
        return new SimpleDateFormat(pattern).format(c.getTime());
    }

	public static Date StrToTime(String str) {
		SimpleDateFormat sd = new SimpleDateFormat("yyyyMMddHHmmss");
		Date sdate = null;
		try {
			sdate = sd.parse(str);
		} catch (ParseException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		return sdate;
	}

